import{_ as e,c as t,a2 as r,o as p}from"./chunks/framework.CehHI-68.js";const i="/archive/payment/alipay/1.png",o="/archive/payment/alipay/2.png",n="/archive/payment/alipay/3.png",s="/archive/payment/alipay/4.png",c="/archive/payment/alipay/5.png",l="/archive/payment/alipay/6.png",h="/archive/payment/alipay/7.png",m="/archive/payment/alipay/8.png",d="/archive/payment/alipay/9.png",_="/archive/payment/alipay/10.png",y="/archive/payment/alipay/11.png",P=JSON.parse('{"title":"支付宝","description":"支付宝网站支付功能","frontmatter":{"title":"支付宝","description":"支付宝网站支付功能"},"headers":[],"relativePath":"archive/enterprise/v1/features/basic/payment/alipay.md","filePath":"zh/archive/enterprise/v1/features/basic/payment/alipay.md","lastUpdated":1731937690000}'),g={name:"archive/enterprise/v1/features/basic/payment/alipay.md"};function u(b,a,v,f,q,k){return p(),t("div",null,a[0]||(a[0]=[r('<div class="warning custom-block"><p class="custom-block-title">注意</p><p>兰空图床仅支持官方支付方式，支付宝需要提供资质申请「电脑网站支付」能力，不支持当面付。</p></div><h2 id="创建支付应用" tabindex="-1">创建支付应用 <a class="header-anchor" href="#创建支付应用" aria-label="Permalink to &quot;创建支付应用&quot;">​</a></h2><p>前往开发者中心，点击 <a href="https://open.alipay.com/develop/pm/create" target="_blank" rel="noreferrer">https://open.alipay.com/develop/pm/create</a> 创建网页移动应用。</p><p><img src="'+i+'" alt="创建应用"></p><h2 id="绑定支付宝支付产品" tabindex="-1">绑定支付宝支付产品 <a class="header-anchor" href="#绑定支付宝支付产品" aria-label="Permalink to &quot;绑定支付宝支付产品&quot;">​</a></h2><p>创建好应用后进入应用，点击「产品绑定」，选择「支付」-&gt; 「电脑网站支付」</p><p><img src="'+o+'" alt="绑定支付"></p><h2 id="开通电脑网站支付" tabindex="-1">开通电脑网站支付 <a class="header-anchor" href="#开通电脑网站支付" aria-label="Permalink to &quot;开通电脑网站支付&quot;">​</a></h2><p>绑定完成后，如果显示「产品开通状态」显示为「未开通」则需要根据支付宝的指引去开通，否则支付功能无法使用。</p><p><img src="'+n+'" alt="开通支付"></p><h2 id="设置加签方式" tabindex="-1">设置加签方式 <a class="header-anchor" href="#设置加签方式" aria-label="Permalink to &quot;设置加签方式&quot;">​</a></h2><p><img src="'+s+'" alt="设置加签方式"><img src="'+c+'" alt="选择加签方式"></p><p>加签方式选择第二项「证书」</p><h2 id="下载证书生成软件" tabindex="-1">下载证书生成软件 <a class="header-anchor" href="#下载证书生成软件" aria-label="Permalink to &quot;下载证书生成软件&quot;">​</a></h2><p><img src="'+l+'" alt="下载证书生成工具"></p><h2 id="生成证书并上传" tabindex="-1">生成证书并上传 <a class="header-anchor" href="#生成证书并上传" aria-label="Permalink to &quot;生成证书并上传&quot;">​</a></h2><p><img src="'+h+'" alt="生成证书并上传"></p><h2 id="下载证书" tabindex="-1">下载证书 <a class="header-anchor" href="#下载证书" aria-label="Permalink to &quot;下载证书&quot;">​</a></h2><p>上传成功后，将证书全部下载。</p><p><img src="'+m+'" alt="下载证书"></p><h2 id="设置回调地址" tabindex="-1">设置回调地址 <a class="header-anchor" href="#设置回调地址" aria-label="Permalink to &quot;设置回调地址&quot;">​</a></h2><p>打开兰空图床后台，进入「系统设置」-&gt;「支付配置」，选择支付宝，复制支付异步回调地址。</p><p><img src="'+d+'" alt="复制异步回调地址"></p><p>然后将其填入支付宝的授权回调地址中。</p><p><img src="'+_+'" alt="填写授权回调地址"></p><h2 id="上传证书" tabindex="-1">上传证书 <a class="header-anchor" href="#上传证书" aria-label="Permalink to &quot;上传证书&quot;">​</a></h2><p>将刚刚下载的三份证书文件上传至兰空图床程序中的 <code>storage/app/certs</code> 目录下，然后打开刚刚由证书生成软件生成的证书目录，复制 <code>应用私钥RSA2048-敏感数据，请妥善保管.txt</code> 文件内容，将其填写到兰空图床后台配置中。</p><p><img src="'+y+'" alt="上传证书"></p><p>至此支付宝电脑网站支付则配置完成了。</p>',29)]))}const $=e(g,[["render",u]]);export{P as __pageData,$ as default};
