import{_ as l,c as a,a2 as c,o}from"./chunks/framework.CehHI-68.js";const n=JSON.parse('{"title":"缓存","description":"配置缓存驱动","frontmatter":{"title":"缓存","description":"配置缓存驱动"},"headers":[],"relativePath":"archive/free/v2/advanced/cache.md","filePath":"zh/archive/free/v2/advanced/cache.md","lastUpdated":1731937013000}'),t={name:"archive/free/v2/advanced/cache.md"};function i(d,e,r,u,s,p){return o(),a("div",null,e[0]||(e[0]=[c('<h1 id="配置缓存" tabindex="-1">配置缓存 <a class="header-anchor" href="#配置缓存" aria-label="Permalink to &quot;配置缓存&quot;">​</a></h1><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>兰空图床支持 redis 或 memcached 作为缓存驱动，通常情况下默认的文件储存已经足够用，但是如果你的图床开启了原图保护功能，强烈更换为下面的任意缓存驱动。<br> 更换驱动非常简单，只需要改变程序中的环境变量文件 <code>.env</code> 即可</p></div><h3 id="使用-redis" tabindex="-1">使用 Redis <a class="header-anchor" href="#使用-redis" aria-label="Permalink to &quot;使用 Redis&quot;">​</a></h3><blockquote><p>缓存驱动名称</p></blockquote><ul><li>CACHE_DRIVER=redis</li></ul><blockquote><p>redis 连接地址(可选)</p></blockquote><ul><li>REDIS_HOST=redis</li></ul><blockquote><p>redis 连接密码(可选)</p></blockquote><ul><li>REDIS_PASSWORD=null</li></ul><blockquote><p>redis 连接端口(可选)</p></blockquote><ul><li>REDIS_PORT=6379</li></ul><h3 id="使用-memcached" tabindex="-1">使用 Memcached <a class="header-anchor" href="#使用-memcached" aria-label="Permalink to &quot;使用 Memcached&quot;">​</a></h3><blockquote><p>缓存驱动名称</p></blockquote><ul><li>CACHE_DRIVER=memcached</li></ul><blockquote><p>memcached 连接地址(可选)</p></blockquote><ul><li>MEMCACHED_HOST=127.0.0.1</li></ul><blockquote><p>memcached 连接端口(可选)</p></blockquote><ul><li>MEMCACHED_PORT=11211</li></ul><blockquote><p>连接用户名(可选)</p></blockquote><ul><li>MEMCACHED_USERNAME=</li></ul><blockquote><p>连接密码(可选)</p></blockquote><ul><li>MEMCACHED_PASSWORD=</li></ul>',22)]))}const m=l(t,[["render",i]]);export{n as __pageData,m as default};
