import{_ as e,c as t,a2 as i,o as l}from"./chunks/framework.CehHI-68.js";const u=JSON.parse('{"title":"基础设置","description":"角色组基础设置","frontmatter":{"title":"基础设置","description":"角色组基础设置"},"headers":[],"relativePath":"archive/free/v2/group/basic.md","filePath":"zh/archive/free/v2/group/basic.md","lastUpdated":1731937013000}'),r={name:"archive/free/v2/group/basic.md"};function o(s,a,c,n,p,d){return l(),t("div",null,a[0]||(a[0]=[i('<h1 id="基础设置" tabindex="-1">基础设置 <a class="header-anchor" href="#基础设置" aria-label="Permalink to &quot;基础设置&quot;">​</a></h1><div class="tip custom-block"><p class="custom-block-title">提示</p><p>兰空图床中的角色组是整个系统的核心部分之一，无论是已注册用户、游客、管理员都受角色组控制。系统安装完成以后会默认创建一个默认的角色组，同时也是游客默认组，设置为游客组的角色组会控制游客上传。并且系统只能设置一个游客组。 设置为默认组的角色组，新用户将会默认归属该角色组。</p></div><h2 id="上传频率限制" tabindex="-1">上传频率限制 <a class="header-anchor" href="#上传频率限制" aria-label="Permalink to &quot;上传频率限制&quot;">​</a></h2><p>在创建、编辑角色组时，「常规配置」中可以看到六个名称为<strong>限制</strong>结尾的输入框，配置及作用：</p><ul><li>并发上传限制(限制上传时队列中同时上传图片数量的限制)</li><li>每分钟上传限制(限制一分钟内最大上传数量)</li><li>每小时上传限制(限制一小时内最大上传数量)</li><li>每天上传限制(限制24小时内最大上传数量)</li><li>每周上传限制(限制7天内最大上传数量)</li><li>每月上传限制(限制一个月内最大上传数量)</li></ul><div class="warning custom-block"><p class="custom-block-title">注意</p><p>上传数量限制，是代表当前时间往前推这段时间内所上传的图片数量限制。例如每周上传限制，是指<strong>七天之前</strong>到<strong>当前时间</strong>的这个范围内，而不是指本周内。</p></div><h2 id="命名规则" tabindex="-1">命名规则 <a class="header-anchor" href="#命名规则" aria-label="Permalink to &quot;命名规则&quot;">​</a></h2><p>角色组可以对上传后的图片按照命名规则表中的规则对图片的物理储存路径、文件名称进行重写，并且相同、不同的规则可以组合使用。</p><h2 id="允许上传的图片类型" tabindex="-1">允许上传的图片类型 <a class="header-anchor" href="#允许上传的图片类型" aria-label="Permalink to &quot;允许上传的图片类型&quot;">​</a></h2><p>兰空图床支持九种图片类型的文件上传，其中会有一些区别。</p><ul><li>PSD、TIP、BMP 格式的图片上传后，启用原图保护的情况下，会以 png 格式输出。</li><li>带有动画的拓展 webp 无法生成缩略图</li><li>ICO 无法生成缩略图</li></ul>',11)]))}const b=e(r,[["render",o]]);export{u as __pageData,b as default};
